package org.zero.jpa.controller;import org.junit.Test;import org.junit.runner.RunWith;import org.junit.runners.JUnit4;import org.springframework.web.client.RestTemplate;import org.zero.jpa.entity.User;import java.util.Date;import java.util.HashMap;import java.util.List;import java.util.Map;/** * @author 水寒 * @date 2020/4/23 */@RunWith(JUnit4.class)public class UserControllerTest {    private static final String URL = "http://localhost:8080/";    @Test    public void create() {        RestTemplate restTemplate = new RestTemplate();        restTemplate.postForObject(URL + "/user", null, Void.class);    }    @Test    public void delete() {}    @Test    public void get() {        RestTemplate restTemplate = new RestTemplate();        User user = restTemplate.getForObject(URL + "/user/" + 1, User.class);        System.out.println(user);    }    @Test    public void update() {        RestTemplate restTemplate = new RestTemplate();        User user = new User();        user.setId(1L);        user.setCreateTime(new Date());        user.setMobile("123");        restTemplate.put(URL + "/user", user);    }    @Test    public void search() {        RestTemplate restTemplate = new RestTemplate();        Map<String, String> params = new HashMap<>();        params.put("name", "水寒");        params.put("mobile", "188");        List<User> user = restTemplate.getForObject(URL + "/user/search?name={name}&mobile={mobile}", List.class, params);        System.out.println(user);    }}